import React from 'react';
import styles from './styles/Hand.module.css';
import Card from './Card';

type HandProps = {
  title: string,
  cards: any[]
};

type HandsProps = {
  hands: any[],
  cards: any[][]
};

const Hand: React.FC<HandProps> = ({ title, cards }) => {
  const getTitle = () => {
    if (cards.length > 0) {
      return (
        <h1 className={styles.title}>{title}</h1>
      );
    }
  }
  return (
    <div className={styles.handContainer}>
      {getTitle()}
      <div className={styles.cardContainer}>
        {cards.map((card: any, index: number) => {
          return (
            <Card key={index} value={card.value} suit={card.suit} hidden={card.hidden} />
          );
        })}
      </div>
    </div>
  );
}

const Hands: React.FC<HandProps> = ({ hands, cards }) => {
  return (
    <div className={styles.handsContainer}>
      <div className={styles.handContainer}>
        {hands.map((hand: any, index: number) => {
          return (
            <Hand title={`Player ${index}`} cards={cards[index]}/>
          );
        })}
      </div>
    </div>
  );
}

export default Hand;
