import React, { useState, useEffect } from 'react';
import Status from './Status';
import Controls from './Controls';
import Hand from './Hand';
import Hands from './Hands';
import jsonData from '../deck.json';

const App: React.FC = () => {
  enum GameState {
    bet,
    init,
    userTurn,
    dealerTurn
  }

  enum Deal {
    p0,
    p1,
    p2,
    p3,
    p4,
    p5,
    dealer,
    hidden
  }

  enum Message {
    bet = 'Place a Bet!',
    hitStand = 'Hit or Stand?',
    bust = 'Bust!',
    userWin = 'You Win!',
    dealerWin = 'Dealer Wins!',
    tie = 'Tie!'
  }

  const data = JSON.parse(JSON.stringify(jsonData.cards));
  const [deck, setDeck]: any[] = useState(data);

  const [address, setAddress] = useState("");

  const [cards, setCards]: any[] = useState(new Array(6));
  const [p0Score, setp0Score] = useState(0);
  const [p0Count, setp0Count] = useState(0);

  const [p1Score, setp1Score] = useState(0);
  const [p1Count, setp1Count] = useState(0);

  const [p2Score, setp2Score] = useState(0);
  const [p2Count, setp2Count] = useState(0);

  const [p3Score, setp3Score] = useState(0);
  const [p3Count, setp3Count] = useState(0);

  const [p4Score, setp4Score] = useState(0);
  const [p4Count, setp4Count] = useState(0);

  const [p5Score, setp5Score] = useState(0);
  const [p5Count, setp5Count] = useState(0);

  const [dealerCards, setDealerCards]: any[] = useState([]);
  const [dealerScore, setDealerScore] = useState(0);
  const [dealerCount, setDealerCount] = useState(0);

  const [balance, setBalance] = useState(100);
  const [bet, setBet] = useState(0);

  const [gameState, setGameState] = useState(GameState.init);
  const [message, setMessage] = useState(Message.bet);
  const [buttonState, setButtonState] = useState({
    hitDisabled: false,
    standDisabled: false,
    resetDisabled: true
  });

  useEffect(() => {
    if (gameState === GameState.init) {
      setGameState(GameState.userTurn);
      setMessage(Message.hitStand);
    }
  }, [gameState]);

  useEffect(() => {
    calculate(cards[0], setp0Score);
    calculate(cards[1], setp1Score);
    calculate(cards[2], setp2Score);
    calculate(cards[3], setp3Score);
    calculate(cards[4], setp4Score);
    calculate(cards[5], setp5Score);

    setp0Count(cards[0].length);
    setp1Count(cards[1].length);
    setp2Count(cards[2].length);
    setp3Count(cards[3].length);
    setp4Count(cards[4].length);
    setp5Count(cards[5].length);
  }, [cards]);

  useEffect(() => {
    calculate(p1Cards, setp1Score);
    setp1Count(p1Count + 1);
  }, [p1Cards]);

  useEffect(() => {
    calculate(p2Cards, setp2Score);
    setp2Count(p2Count + 1);
  }, [p2Cards]);

  useEffect(() => {
    calculate(p3Cards, setp3Score);
    setp3Count(p3Count + 1);
  }, [p3Cards]);

  useEffect(() => {
    calculate(p4Cards, setp4Score);
    setp4Count(p4Count + 1);
  }, [p4Cards]);

  useEffect(() => {
    calculate(p5Cards, setp5Score);
    setp5Count(p5Count + 1);
  }, [p5Cards]);

  useEffect(() => {
    calculate(dealerCards, setDealerScore);
    setDealerCount(dealerCount + 1);
  }, [dealerCards]);

  useEffect(() => {
    if (gameState === GameState.userTurn) {
      if (p0Score === 21) {
        buttonState.hitDisabled = true;
        setButtonState({ ...buttonState });
      }
      else if (p0Score > 21) {
        bust();
      }
    }
  }, [p0Count]);

  useEffect(() => {
    if (gameState === GameState.dealerTurn) {
      if (dealerScore >= 17) {
        checkWin();
      }
      else {
        drawCard(Deal.dealer);
      }
    }
  }, [dealerCount]);

  const resetGame = () => {
    console.clear();
    setDeck(data);

    setp0Cards([]);
    setp0Score(0);
    setp0Count(0);

    setp1Cards([]);
    setp1Score(0);
    setp1Count(0);

    setp2Cards([]);
    setp2Score(0);
    setp2Count(0);

    setp3Cards([]);
    setp3Score(0);
    setp3Count(0);

    setp4Cards([]);
    setp4Score(0);
    setp4Count(0);

    setp5Cards([]);
    setp5Score(0);
    setp5Count(0);

    setDealerCards([]);
    setDealerScore(0);
    setDealerCount(0);

    setBet(0);

    setGameState(GameState.bet);
    setMessage(Message.bet);
    setButtonState({
      hitDisabled: false,
      standDisabled: false,
      resetDisabled: true
    });
  }

  const placeBet = (amount: number) => {
    setBet(amount);
    setBalance(Math.round((balance - amount) * 100) / 100);
    setGameState(GameState.init);
  }

  const drawCard = (dealType: Deal) => {
    if (deck.length > 0) {
      const randomIndex = Math.floor(Math.random() * deck.length);
      const card = deck[randomIndex];
      deck.splice(randomIndex, 1);
      setDeck([...deck]);
      console.log('Remaining Cards:', deck.length);
      switch (card.suit) {
        case 'spades':
          dealCard(dealType, card.value, '♠');
          break;
        case 'diamonds':
          dealCard(dealType, card.value, '♦');
          break;
        case 'clubs':
          dealCard(dealType, card.value, '♣');
          break;
        case 'hearts':
          dealCard(dealType, card.value, '♥');
          break;
        default:
          break;
      }
    }
    else {
      alert('All cards have been drawn');
    }
  }

  const dealCard = (dealType: Deal, value: string, suit: string) => {
    switch (dealType) {
      case Deal.p0:
        p0Cards.push({ 'value': value, 'suit': suit, 'hidden': false });
        setp0Cards([...p0Cards]);
        break;
      case Deal.p1:
        p1Cards.push({ 'value': value, 'suit': suit, 'hidden': false });
        setp1Cards([...p0Cards]);
        break;
      case Deal.p2:
        p2Cards.push({ 'value': value, 'suit': suit, 'hidden': false });
        setp2Cards([...p0Cards]);
        break;
      case Deal.p3:
        p3Cards.push({ 'value': value, 'suit': suit, 'hidden': false });
        setp3Cards([...p0Cards]);
        break;
      case Deal.p4:
        p4Cards.push({ 'value': value, 'suit': suit, 'hidden': false });
        setp4Cards([...p0Cards]);
        break;
      case Deal.p5:
        p5Cards.push({ 'value': value, 'suit': suit, 'hidden': false });
        setp5Cards([...p0Cards]);
        break;
      case Deal.dealer:
        dealerCards.push({ 'value': value, 'suit': suit, 'hidden': false });
        setDealerCards([...dealerCards]);
        break;
      case Deal.hidden:
        dealerCards.push({ 'value': value, 'suit': suit, 'hidden': true });
        setDealerCards([...dealerCards]);
        break;
      default:
        break;
    }
  }

  const revealCard = () => {
    dealerCards.filter((card: any) => {
      if (card.hidden === true) {
        card.hidden = false;
      }
      return card;
    });
    setDealerCards([...dealerCards])
  }

  const calculate = (cards: any[], setScore: any) => {
    let total = 0;
    cards.forEach((card: any) => {
      if (card.hidden === false && card.value !== 'A') {
        switch (card.value) {
          case 'K':
            total += 10;
            break;
          case 'Q':
            total += 10;
            break;
          case 'J':
            total += 10;
            break;
          default:
            total += Number(card.value);
            break;
        }
      }
    });
    const aces = cards.filter((card: any) => {
      return card.value === 'A';
    });
    aces.forEach((card: any) => {
      if (card.hidden === false) {
        if ((total + 11) > 21) {
          total += 1;
        }
        else if ((total + 11) === 21) {
          if (aces.length > 1) {
            total += 1;
          }
          else {
            total += 11;
          }
        }
        else {
          total += 11;
        }
      }
    });
    setScore(total);
  }

  const hit = () => {
    drawCard(Deal.p0);
    drawCard(Deal.p1);
    drawCard(Deal.p2);
    drawCard(Deal.p3);
    drawCard(Deal.p4);
    drawCard(Deal.p5);
  }

  const stand = () => {
    buttonState.hitDisabled = true;
    buttonState.standDisabled = true;
    buttonState.resetDisabled = false;
    setButtonState({ ...buttonState });
    setGameState(GameState.dealerTurn);
    revealCard();
  }

  const bust = () => {
    buttonState.hitDisabled = true;
    buttonState.standDisabled = true;
    buttonState.resetDisabled = false;
    setButtonState({ ...buttonState });
    setMessage(Message.bust);
  }

  const checkWin = () => {
    if (p0Score > dealerScore || dealerScore > 21) {
      setBalance(Math.round((balance + (bet * 2)) * 100) / 100);
      setMessage(Message.userWin);
    }
    else if (dealerScore > p0Score) {
      setMessage(Message.dealerWin);
    }
    else {
      setBalance(Math.round((balance + (bet * 1)) * 100) / 100);
      setMessage(Message.tie);
    }
  }

  return (
    <>
      <Status message={message} balance={balance} />
      <Controls
        balance={balance}
        gameState={gameState}
        buttonState={buttonState}
        betEvent={placeBet}
        hitEvent={hit}
        standEvent={stand}
        resetEvent={resetGame}
      />
      <Hand title={`Dealer's Hand (${dealerScore})`} cards={dealerCards} />
      <Hands cardsArr= {[p0Cards, p1Cards,p2Cards,p3Cards,p4Cards,p5Cards]} scores= {[p0Score, p1Score,p2Score, p3Score,p4Score, p5Score]}/>
    </>
  );
}

export default App;
